package delimiter

import (
	"fmt"
	"reflect"
	"testing"

	"github.com/seriozhakorneev/gpt-text-delimiter/pkg/tokenizer"
)

const (
	limit = 4097
)

func TestDelimiter_New(t *testing.T) {
	expLimit := limit

	encoder, err := tokenizer.NewGPT3()
	if err != nil {
		t.Fatal("Unexpected error in testing:", err)
	}

	delimiter, err := New(encoder, -1)
	if delimiter != nil {
		t.Fatal("Expected nil delimiter\nGot:", delimiter)
	}

	expErr := fmt.Errorf("token limit should be more than 0")
	if !reflect.DeepEqual(err, expErr) {
		t.Fatalf("Expected error: %s\nGot:%s", expErr, err)
	}

	delimiter, err = New(encoder, expLimit)
	if err != nil {
		t.Fatal("Unexpected error in testing:", err)
	}

	if delimiter.limit != expLimit {
		t.Fatalf("Expected limit: %d\nGot: %d", expLimit, delimiter.limit)
	}

	if delimiter.e == nil {
		t.Fatal("Expected encoder in delimiter\nGot nil")
	}
}

func TestDelimiter_Split_Errors(t *testing.T) {
	expEncodeErr := fmt.Errorf("encode error")
	encoder := MockEncoder{EncodeFunc: func(s string) ([]int, error) {
		return nil, expEncodeErr
	}}

	d := &Delimiter{
		limit: 0,
		e:     encoder,
	}

	expSplitErr := fmt.Errorf("delimiter - Split - d.e.Encode: %w", expEncodeErr)
	split, err := d.Split("test string")
	if split != nil {
		t.Fatal("Expected split: nil\nGot:", split)
	}

	if !reflect.DeepEqual(err, expSplitErr) {
		t.Fatalf("Expected error: %s\nGot: %s", expSplitErr, err)
	}
}

